const clientId = "1755523544f9437e937f776f3210bde4";
const redirctURL = "http://localhost:3000/";

let accessToken;
let expiresIn;

const Spotify = {
  getAccessToken() {
    if (accessToken) {
      return accessToken;
    } else {
      const url = window.location.href;
      console.log(url);
      let urlAccessToken = url.match(/access_token=([^&]*)/);
      let urlExpiresIn = url.match(/expires_in=([^&]*)/);
      if (urlAccessToken && urlExpiresIn) {
        accessToken = urlAccessToken;
        expiresIn = urlExpiresIn;
        window.setTimeout(() => (accessToken = ""), expiresIn * 1000);
        window.history.pushState("Access Token", null, "/");
      } else {
        window.location.href = `https://accounts.spotify.com/authorize?client_id=${clientId}&response_type=token&scope=playlist-modify-public&redirect_uri=${redirctURL}
            `;
      }
    }
  },

  search(term) {
    console.log(accessToken);
    return fetch(`https://api.spotify.com/v1/search?type=track&q=${term}`, {
      headers: { Authorization: `Bearer ${accessToken}` }
    })
      .then(response => {
        return response.json();
      })
      .then(jsonResponse => {
        if (jsonResponse.tracks) {
          return jsonResponse.tracks.map(track => {
            console.log(track);
            return {
              id: track.id,
              name: track.name,
              artist: track.artist,
              album: track.album
            };
          });
        }
      });
  }
};
export default Spotify;
