const clientId = "1755523544f9437e937f776f3210bde4";
const redirctURL = "http://localhost:3000/";

let accessToken;
let expiresIn;

const Spotify = {
  getAccessToken() {
    if (accessToken) {
      return accessToken;
    } else {
      const url = window.location.href;
      console.log(url);
      let urlAccessToken = url.match(/access_token=([^&]*)/);
      let urlExpiresIn = url.match(/expires_in=([^&]*)/);
      if (urlAccessToken && urlExpiresIn) {
        accessToken = urlAccessToken;
        expiresIn = urlExpiresIn;
        console.log(accessToken);
        window.setTimeout(() => (accessToken = ""), expiresIn * 1000);
        window.history.pushState("Access Token", null, "/");
        return accessToken;
      } else {
        let spotifyUrl = `https://accounts.spotify.com/authorize?client_id=${clientId}&response_type=token&scope=playlist-modify-public&redirect_uri=${redirctURL}`;
        window.location = spotifyUrl;
      }
    }
  },

  search(term) {
    console.log(accessToken);
    return fetch(`https://api.spotify.com/v1/search?type=track&q=${term}`, {
      headers: { Authorization: `Bearer ${accessToken}` }
    })
      .then(
        response => {
          if (response.ok) {
            return response.json();
          }
          throw new Error("Request failed!");
        },
        networkError => {
          console.log(networkError.message);
        }
      )
      .then(jsonResponse => {
        let tracksJSON = [];
        if (jsonResponse.tracks.items) {
          tracksJSON = jsonResponse.track.items.map(track => ({
            id: track.id,
            name: track.name,
            artist: track.artist,
            album: track.album,
            uri: track.uri
          }));
        }
      });
  },

  savePlaylist(playlistName, trackURIs) {
    if (!playlistName || !trackURIs) return;
    const usersAccessToken = accessToken;
    const headers = { Authorization: `Bearer ${accessToken}` };
    const usersID = "";
    fetch("https://accounts.spotify.com/authorize");
  }
};
export default Spotify;
