const clientId = "1755523544f9437e937f776f3210bde4";
const redirctURL = "http://localhost:3000/";
const authEndpoint = "https//accounts.spotify.com/authorize";
let accessToken;
let expiresIn;

const Spotify = {
  getAccessToken() {
    if (accessToken) {
      return accessToken;
    } else {
      if (
        window.location.href.match(/access_token=([^&]*)/) &&
        window.location.href.match(/expires_in=([^&]*)/)
      ) {
        accessToken = window.location.href.match(/access_token=([^&]*)/);
        expiresIn = window.location.href.match(/expires_in=([^&]*)/);
        console.log(accessToken);
        window.setTimeout(() => (accessToken = ""), expiresIn * 1000);
        window.history.pushState("Access Token", null, "/");
        return accessToken;
      } else {
        let spotifyUrl = `https://accounts.spotify.com/authorize?client_id=${clientId}&response_type=token&scope=playlist-modify-public&redirect_uri=${redirctURL}`;
        window.location = spotifyUrl;
      }
    }
  },

  search(term) {
    console.log(accessToken);
    return fetch(`https://api.spotify.com/v1/search?type=track&q=${term}`, {
      headers: { Authorization: `Bearer ${accessToken}` }
    })
      .then(
        response => {
          if (response.ok) {
            return response.json();
          }
          throw new Error("Request failed!");
        },
        networkError => {
          console.log(networkError.message);
        }
      )
      .then(jsonResponse => {
        let tracksJSON = [];
        if (jsonResponse.tracks.items) {
          tracksJSON = jsonResponse.track.items.map(track => ({
            id: track.id,
            name: track.name,
            artist: track.artist,
            album: track.album,
            uri: track.uri
          }));
        }
        return tracksJSON;
      });
  },

  savePlaylist(playlistName, trackURIs) {
    if (!playlistName || !trackURIs) {
      return;
    } else {
      const userUrl = "https://api.spotify.com/v1/me";
      const headers = {
        Authorization: `Bearer ${accessToken}`
      };
      let userId = undefined;
      let playlistId = undefined;
      fetch(userUrl, {
        headers: headers
      })
        .then(response => response.json())
        .then(jsonResponse => (userId = jsonResponse.id))
        .then(() => {
          const createPlaylistUrl = `https://api.spotify.com/v1/users/${userId}/playlists`;
          fetch(createPlaylistUrl, {
            method: "POST",
            headers: headers,
            body: JSON.stringify({
              name: playlistName
            })
          })
            .then(response => response.json())
            .then(jsonResponse => (playlistId = jsonResponse.id))
            .then(() => {
              const addPlaylistTracksUrl = `https://api.spotify.com/v1/users/${userId}/playlists/${playlistId}/tracks`;
              fetch(addPlaylistTracksUrl, {
                method: "POST",
                headers: headers,
                body: JSON.stringify({
                  uris: trackURIs
                })
              });
            });
        });
    }
  }
};

export default Spotify;
